import { GET_PRODUCT, FETCH_PRODUCTS } from "./actionType";
const initialState = {
  products: [],
  filterProducts: [],
}

export default function (state = initialState, action) {
  console.log('enter');
  switch (action.type) {
    case FETCH_PRODUCTS:
      return { ...state, products: action.payload }
    case GET_PRODUCT:
      return action.payload;
    default:
      return state
  }
}
