import { products } from '../../../api/DB.json'
import { GET_PRODUCT } from "./actionType";
const initialState = {
  products,
}

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_PRODUCT:
      return action.payload;
    default:
      return state
  }
}
