import React, { useState } from 'react'
import { connect } from 'react-redux';

/**
 * Build auto complete component
 * Display it on the screen
 * Build className that represent js Object 
 * Build input tag 
 * receive data to filter   /* remember your get the your data through object called props
 * Look what other did and optimize
 */

export const AutoComplete = ({ products }) => {
  const [filterProducts, setFilterProducts] = useState(products);
  const [text, setText] = useState('')
  console.log(products);
  const onChange = (e) => {
    const value = e.target.value;
    const filterByName = products.filter(product => product.title.includes(value));
    setText(value)
    setFilterProducts(filterByName);
  }

  return (
    <ul className='auto-complete' style={{ position: "relative", width: "500px", maxWidth: '400px', textAlign: "left" }} >
      <input style={{ width: "100%" }} value={text} onChange={onChange} type='text' placeholder='Search...' />
      <div className="dropdown-content" style={{ position: "absolute", background: "#eee", width: "100%" }}>
        {text ? filterProducts.map(product => <li>{product.title}</li>) : ""}

      </div>
    </ul>
  )
}

const mapStateToProps = (state) => ({
  products: state.cart.products,
});


export default connect(mapStateToProps, null)(AutoComplete);
