import React, { useState, useEffect } from 'react';
import axios from 'axios'
import { connect } from 'react-redux';
import { getProduct } from "../../redux_store/reducers/cart/actions";

export const AutoComplete = ({ getProduct }) => {

  const [search, setSearch] = useState('');
  const [products, setProducts] = useState([]);
  const [filterProducts, setFilterProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        'https://bali-product-server.herokuapp.com/api/products',
      );
      setProducts(result.data);
    };

    fetchData();
  }, []);


  useEffect(() => {
    setFilterProducts(products.filter(product => {
      const productTitle = product.title.toLowerCase();
      productTitle.includes(search.toLowerCase())
    }
    ))
  }, [search]);



  const onChange = (e) => {
    const value = e.target.value
    const filterByName = filterProducts.filter(product => product.title.toLowerCase().includes(value));
    setSearch(value)
    setFilterProducts(filterByName);
  }
  console.log(filterProducts);
  return (
    <div className='auto-complete'>
      <input onChange={onChange} type='text' placeholder='Search...' /><i className="fas fa-search" style={{ position: "absolute", top: "25%", right: "5%" }}></i>

      {search && filterProducts.length !== 0 ? (<ul className="dropdown">
        {filterProducts.map((product, index) => <li key={index} onClick={() => getProduct(product)}>{product.title}</li>)}
      </ul>)
        : ""}
    </div>
  )
}





const mapDispatchToProps = {
  getProduct,
}


export default connect(null, mapDispatchToProps)(AutoComplete);
