import { products } from '../../../api/DB.json'
import { SHOW_BY_TEXT } from '../VisibilityFilterReducer/actionsTypes'
import { getVisibleProducts } from '../../../utils/getVisibleProducts';

const initialState = {
  products,
}

export default function (state = initialState, action) {
  switch (action.type) {
    case SHOW_BY_TEXT:
      return getVisibleProducts(state.products, action.filter);
    default:
      return state
  }
}
