import { SHOW_ALL, SHOW_BY_TEXT } from "../redux-store/reducers/VisibilityFilterReducer/actionsTypes";


export const getVisibleProducts = (products, filter) => {
  switch (filter) {
    case SHOW_ALL:
      return products;
    case SHOW_BY_TEXT:
      return getProductByText(products, filter.payload)
    default:
      return products
  }
}

export const getProductByText = (products, text) => {
  return products.filter(product => {
    return product.title.toLowerCase().includes(text.toLowerCase())
  });
}

export default getVisibleProducts
