import React, { useState } from 'react'
import { connect } from 'react-redux';
import { setVisibilityFilter } from "../../redux_store/reducers/cart/actions";



export const AutoComplete = ({ products, setVisibilityFilter }) => {
  const [filterProducts, setFilterProducts] = useState(products);
  const [text, setText] = useState('')
  const onChange = (e) => {
    const value = e.target.value;
    setVisibilityFilter({ type: "SHOW_BY_TEXT", value });
    const filterByName = products.filter(product => {
      return product.title.toLowerCase().includes(value.toLowerCase())
    });
    setText(value)
    setFilterProducts(filterByName);
  }
  console.log(filterProducts);
  return (
    <div className='auto-complete'>
      <input value={text} onChange={onChange} type='text' placeholder='Search...' /><i className="fas fa-search" style={{ position: "absolute", top: "25%", right: "2%" }}></i>

      {text && filterProducts.length !== 0 ? (<ul className="dropdown">
        {filterProducts.map((product, index) => <li key={index}>{product.title}</li>)}
      </ul>)
        : ""}
    </div>
  )
}

const mapStateToProps = (state) => ({
  products: state.cart.products,
});

const mapDispatchToProps = {
  setVisibilityFilter,

}


export default connect(mapStateToProps, mapDispatchToProps)(AutoComplete);
