import React, { useState, useEffect } from 'react';
import axios from 'axios'
import { connect } from 'react-redux';
import { getProduct } from "../../redux_store/reducers/cart/actions";

export const AutoComplete = ({ getProduct }) => {



  const [filterProducts, setFilterProducts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        'https://bali-product-server.herokuapp.com/api/products',
      );
      console.log(result.data);
      setFilterProducts(result.data);
    };

    fetchData();
  }, []);
  const [text, setText] = useState('')

  const onChange = (e) => {
    const value = e.target.value;
    const filterByName = products.filter(product => product.title.includes(value));
    setText(value)
    setFilterProducts(filterByName);
  }
  console.log(filterProducts);
  return (
    <div className='auto-complete'>
      <input value={text} onChange={onChange} type='text' placeholder='Search...' /><i className="fas fa-search" style={{ position: "absolute", top: "25%", right: "5%" }}></i>

      {text && filterProducts.length !== 0 ? (<ul className="dropdown">
        {filterProducts.map((product, index) => <li key={index} onClick={() => getProduct(product)}>{product.title}</li>)}
      </ul>)
        : ""}
    </div>
  )
}





const mapDispatchToProps = {
  getProduct,
}


export default connect(null, mapDispatchToProps)(AutoComplete);
