import React, { useState } from 'react'
import { connect } from 'react-redux';

/**
 * Build auto complete component
 * Display it on the screen
 * Build className that represent js Object 
 * Build input tag 
 * receive data to filter   /* remember your get the your data through object called props
 * Look what other did and optimize
 */

export const AutoComplete = ({ products }) => {
  const [filterProducts, setFilterProducts] = useState(products);
  const [text, setText] = useState('')
  const onChange = (e) => {
    const value = e.target.value;
    const filterByName = products.filter(product => product.title.includes(value));
    setText(value)
    setFilterProducts(filterByName);
  }

  return (
    <div className='auto-complete' style={{ position: "relative", width: "500px", maxWidth: '400px', textAlign: "left" }} >
      <input value={text} onChange={onChange} type='text' placeholder='Search...' />
      <ul className="dropdown">
        {text ? filterProducts.map((product, index) => <li key={index}>{product.title}</li>) : ""}

      </ul>
    </div>
  )
}

const mapStateToProps = (state) => ({
  products: state.cart.products,
});


export default connect(mapStateToProps, null)(AutoComplete);
