import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from "react-redux";
import ProductItem from '../../ProductItem';
import { addProduct } from '../../../redux_store/reducers/user/actions';
import axios from 'axios';

ProductsContainer.propTypes = {
  products: PropTypes.arrayOf(PropTypes.shape({
    id: PropTypes.number.isRequired,
    title: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
    quantity: PropTypes.number.isRequired
  })).isRequired,
};

function ProductsContainer({ addProduct }) {

  const [products, setProducts] = useState({});

  useEffect(async () => {
    const result = await axios.get(
      'https://hn.algolia.com/api/v1/search?query=redux'
    );
    setProducts(result.data);
  }, []);

  return (
    <div className="products container">
      {products.map(product => (<ProductItem key={product.id} product={product} addProduct={addProduct} />))}
    </div>
  );
}



const mapDispatchToProps = {
  addProduct,
}



export default connect(null, mapDispatchToProps)(ProductsContainer);
