import React, { useState } from 'react'
import { connect } from 'react-redux';

/**
 * Build auto complete component
 * Display it on the screen
 * Build className that represent js Object 
 * Build input tag 
 * receive data to filter   /* remember your get the your data through object called props
 * er 
 */

export const AutoComplete = ({ products }) => {
  const [filterProducts, setFilterProducts] = useState(products.sort());
  console.log(products);
  const onChange = (e) => {
    const value = e.target.value;
    const filteredProducts = filterProducts.filter(product => product.title.includes(value));
    console.log(filteredProducts);
    setFilterProducts(filterProducts)
  }

  return (
    <div className='auto_complete' >
      <input onChange={onChange} type='text' placeholder='Search...' />
      {filterProducts.map(product => product.title)}
    </div>
  )
}

const mapStateToProps = (state) => ({
  products: state.cart.products,
});


export default connect(mapStateToProps, null)(AutoComplete);
