import React from 'react'
import { connect, useSelector } from 'react-redux';

/**
 * Build auto complete component
 * Display it on the screen
 * Build className that represent js Object 
 * Build input tag 
 * receive data to filter   /* remember your get the your data through object called props
 * 
 */

export function AutoComplete() {
  const products = useSelector(state => state.cart.products)
  return (
    <div className='auto_complete' >
      <input type='text' placeholder='Search...' />

    </div>
  )
}

const mapStateToProps = (state) => ({
  products: state.cart.products
});


// export default connect(mapStateToProps, null)(AutoComplete);
export default AutoComplete;
