import { ADD_PRODUCT } from "./actions";

const initialState = {
  products: []
}

export function productsReducer(state = initialState, action) {
  switch (action.type) {
    case ADD_PRODUCT:
      return [...state, action.payload];
    default:
      return state
  }
}
