import React, { useState } from 'react'
import { connect } from 'react-redux';
import { SHOW_BY_TEXT, SHOW_ALL } from '../../redux_store/reducers/VisibilityFilterReducer/actionsTypes';
import { getVisibleProducts } from '../../utils/getVisibleProducts';



export const AutoComplete = ({ products }) => {
  const [filterProducts, setFilterProducts] = useState(products);
  const [text, setText] = useState('')
  const onChange = (e) => {
    const value = e.target.value;
    // setVisibilityFilter(value);
    // const filterByName = products.filter(product => {
    //   return product.title.toLowerCase().includes(value.toLowerCase())
    // });
    setText(value)
    // setFilterProducts(filterByName);
  }
  console.log(filterProducts);
  return (
    <div className='auto-complete'>
      <input value={text} onChange={onChange} type='text' placeholder='Search...' /><i className="fas fa-search" style={{ position: "absolute", top: "25%", right: "2%" }}></i>

      {text && filterProducts.length !== 0 ? (<ul className="dropdown">
        {filterProducts.map((product, index) => <li key={index}>{product.title}</li>)}
      </ul>)
        : ""}
    </div>
  )
}

const mapStateToProps = (state) => ({
  products: getVisibleProducts(state.products, { type: SHOW_ALL, payload: text }),
});



const mapDispatchToProps = {
  // getProductsByText
}


export default connect(mapStateToProps)(AutoComplete);
