import { GET_PRODUCT, FETCH_PRODUCTS, FILTER_PRODUCTS_BY_TITLE } from "./actionType";
import { filteredProducts } from "../../../utils/filter";
const initialState = {
  products: [],
  filterProducts: [],
}

export default function (state = initialState, action) {
  switch (action.type) {
    case FETCH_PRODUCTS:
      return { ...state, products: action.payload, filterProducts: action.payload }
    case FILTER_PRODUCTS_BY_TITLE:
      const filterProductsByTitle = filteredProducts(state.products, action.payload);
      console.log(filterProducts);
      return {
        ...state,
        // filterProducts: filterProductsByTitle,

      }

    case GET_PRODUCT:
    // return { ...state, products: [action.payload] };
    default:
      return state
  }
}
