import { ADD_PRODUCT } from "./actionTypes";


const initialState = {
  products: []
}

export default function (state = initialState, action) {
  switch (action.type) {
    case ADD_PRODUCT:

      // return [...state, action.payload];
      return [1, 2, 3]
    default:
      return state
  }
}
